max(
  node_memory_MemTotal_bytes{ job="node-exporter", instance="$node:$port"}
  - node_memory_MemFree_bytes{ job="node-exporter", instance="$node:$port"}
  - node_memory_Buffers_bytes{ job="node-exporter", instance="$node:$port"}
  - node_memory_Cached_bytes{ job="node-exporter", instance="$node:$port"}
)

max(
  (
    (
      node_memory_MemTotal_bytes{ job="node-exporter", instance="$node:$port"}
    - node_memory_MemFree_bytes{ job="node-exporter", instance="$node:$port"}
    - node_memory_Buffers_bytes{ job="node-exporter", instance="$node:$port"}
    - node_memory_Cached_bytes{ job="node-exporter", instance="$node:$port"}
    )
    / node_memory_MemTotal_bytes{ job="node-exporter", instance="$node:$port"}
  ) * 100)
